(1, 1, 'Sr WordPress', '', 'http://wordpress.org/', '', '2013-07-26 20:38:05', '2013-07-26 20:38:05', 'Hola, esto es un comentario.\nPara borrar un comentario simplemente accede y revisa los comentarios de la entrada. Ahí tendrás la opción de editarlo o borrarlo.', 0, '1', '', '', 0, 0),
(2, 5, 'Mr WordPress', '', 'http://wordpress.org/', '', '2013-07-25 04:09:57', '2013-07-25 04:09:57', 'Hi, this is a comment.\nTo delete a comment, just log in and view the post&#039;s comments. There you will have the option to edit or delete them.', 0, '1', '', '', 0, 0),
(3, 39, 'Mr WordPress', '', 'http://wordpress.org/', '', '2013-07-29 12:50:53', '2013-07-29 12:50:53', 'Hi, this is a comment.\nTo delete a comment, just log in and view the post&#039;s comments. There you will have the option to edit or delete them.', 0, '1', '', '', 0, 0);
